(103, 'Número de términos (En función del 1er termino, el último término y la diferencia)', '$$n=\\frac{a_n-a_1}{d}+1$$', NULL, 0, 8, '2023-04-06 20:22:14', '2023-04-06 20:22:14'),
(104, 'raiz de una potencia', '$$\\sqrt[n]{\\frac{a}{b}}=\\frac{\\sqrt[n]{a}}{\\sqrt[n]{b}}$$', NULL, 0, 2, '2023-04-06 20:24:21', '2023-04-06 20:24:21'),
(105, 'Número de términos (En función de la suma de términos, del 1er y último término)', '$$n=\\frac{2\\cdot{S_n}}{a_1+a_n}$$', NULL, 0, 8, '2023-04-06 20:25:29', '2023-04-06 20:25:29'),
(106, 'raiz de un producto de factores iguales', '$$\\sqrt[n]{a}\\cdot \\sqrt[n]{a}=a^\\frac{2}{n}$$', '<p>sn</p>', 0, 2, '2023-04-06 20:27:27', '2023-04-06 20:27:27'),
