(318, 'Divisibilidad del 2', '$$Todo\\:número\\:par\\\\Que\\:termina\\:en\\:0,\\:2,\\:4,\\:6\\:u\\:8,\\:es\\:divisible\\:entre\\:2.$$', '<p>Por ejemplo:</p><p>26 es divisible entre 2. Termina en 6, un múltiplo de 2.</p><p>58 es divisible entre 2. Termina en 8, un múltiplo de 2.</p><p>84 es divisible entre 2. Termina en 4, un múltiplo de 2.</p><p>&nbsp;</p>', 0, 48, '2023-04-26 15:42:52', '2023-04-26 15:50:23'),
(319, 'Divisibilidad del 3', '$$Si\\:la\\:suma\\:de\\:sus\\:dígitos\\:es\\:igual\\:a\\:3\\:o\\:a\\:un\\:múltiplo\\:de\\:3.$$', '<p>Por ejemplo:</p><p>108. Si sumamos sus dígitos tenemos: 1+0+8=9. Por tanto, 108 es divisible entre 3.</p>', 0, 48, '2023-04-26 15:46:59', '2023-04-26 15:46:59'),
(320, 'Divisibilidad del 4', '$$Si\\:sus\\:últimos\\:dos\\:dígitos\\:son\\:0\\:o\\:un\\:múltiplo\\:de\\:4$$', '<p>Por ejemplo:</p><p>300 y 516 son divisibles entre 4 porque terminan en 00 y en 16, respectivamente, siendo este último un múltiplo de 4 (16=4*4).</p>', 0, 48, '2023-04-26 15:52:57', '2023-04-26 15:52:57'),
(321, 'CALOR', '$$Q\\:=m\\cdot C_V\\cdot \\left(T_2-T_1\\right)$$', '<p>SN</p>', 0, 92, '2023-04-26 15:53:57', '2023-04-26 16:01:49'),
(322, 'Divisibilidad del 5', '$$Si\\:su\\:último\\:dígito\\:es\\:un\\:5\\:o\\:un\\:0$$', '<p>Por ejemplos:</p><p>35, 2145, 40 son divisibles por 5 porque terminan en 5 o en 0.</p>', 0, 48, '2023-04-26 15:58:42', '2023-04-26 16:00:20'),
(323, 'constante', '$$\\gamma \\:=\\frac{C_V}{C_P}\\:$$', '<p>sn</p>', 0, 92, '2023-04-26 16:01:10', '2023-04-26 16:18:59'),
(324, 'Velocidad angular', '$$w=\\frac{\\theta}{t};\\:\\:\\:\\:\\:\\:w=\\frac{2\\pi}{T}\\\\w=2\\pi·f\\:;\\:w=\\frac{V}{r}$$', '<p>fr</p>', 0, 58, '2023-04-26 16:03:06', '2023-06-01 16:16:47'),
(325, 'Divisibilidad del 6', '$$Un\\:número\\:debe\\:cumplir\\:con\\:los\\:criterios\\:de\\:divisibilidad\\:del\\:2\\:y\\:del\\:3\\:para\\:ser\\:divisible\\:entre\\:6$$', '<p>Por ejemplo:</p><p>1.440 termina en 0 y, a su vez, al sumar sus dígitos (1+4+4) obtenemos 9 que es un múltiplo de 3.</p>', 0, 48, '2023-04-26 16:09:04', '2023-04-26 16:09:04'),
(326, 'Velocidad tangencial o lineal', '$$V=r·w\\:;\\:\\:\\:V=\\frac{r·\\theta}{t}\\\\V=\\frac{2\\pi ·r}{T}\\:;\\:V=2\\pi·r·f$$', '<p>gt</p>', 0, 58, '2023-04-26 16:10:43', '2023-06-01 16:20:29'),
(327, 'CONSTANTE GENERAL DE GASES', '$$R\\:=C_P-C_V$$', NULL, 0, 92, '2023-04-26 16:21:50', '2023-04-26 16:21:50'),
(328, 'Aceleración centrípeta', '$$a_c=w^2·r\\\\a_c=\\frac{V^2}{r}\\\\a_c=V·w\\\\$$', NULL, 0, 58, '2023-04-26 16:25:25', '2023-06-01 16:24:31'),
(329, 'Período y Frecuencia', '$$f=\\frac{1}{T}\\:\\\\T=\\frac{1}{f}$$', NULL, 0, 58, '2023-04-26 16:30:28', '2023-06-01 16:25:24'),
(330, 'Divisibilidad del 7', '$$Se\\:debe\\:multiplicar\\:el\\:último\\:dígito\\:por\\:2\\:y\\:restarlo\\:al\\:número\\:que\\:conforman\\:los\\:demás\\:dígitos.\\\\Esto,\\:hasta\\:que\\:queda\\:un\\:número\\:de\\:solo\\:un\\:dígito.\\\\Si\\:este\\:es\\:un\\:0\\:o\\:un\\:7,\\:el\\:número\\:es\\:divisible\\:entre\\:7.$$', '<p>El criterio de divisibilidad del 7 establece que un número es divisible por 7 si se aplica el doble a la última cifra y la diferencia entre el resto del número y el resultado es igual a cero o divisible por 7.&nbsp;</p><p>Por ejemplo:</p><p>49 - 6(2) = 49 - 12 = 37, que es divisible por 7</p>', 0, 48, '2023-04-26 16:38:12', '2023-05-02 16:02:14'),
(331, 'Distancia Angular', '$$\\theta=w_0·t\\pm\\frac{1}{2}·\\alpha·t^2$$', '<p>se</p>', 0, 59, '2023-04-26 19:42:07', '2023-08-25 00:41:18'),
(637, 'Interés simple', '$$I=C_o\\cdot i\\cdot n$$', NULL, 0, 100, '2023-09-06 19:39:21', '2023-09-07 22:22:51'),
(332, 'Velocidad angular final o instantánea', '$$w_f=w_o\\pm \\alpha·t$$', '<p>cf</p>', 0, 59, '2023-04-27 16:11:08', '2023-08-25 00:42:17'),
(636, 'Velocidad final en función de la distancia angular', '$$w_f=w_o\\pm \\alpha·t$$', NULL, 0, 59, '2023-08-25 00:48:32', '2023-08-25 00:48:32'),
(333, 'Aceleración tangencial y aceleración normal', '$$a_t=\\alpha·r\\:\\:;\\:\\:a_N=\\sqrt{a_c^2+a_t^2}\\\\a_c \\:\\:en\\:un\\:instante\\:\'\'t\'\' del\\:movimiento$$', '<p>aw</p>', 0, 59, '2023-04-27 16:17:23', '2023-04-27 18:03:43'),
(334, 'Dilatación Lineal', '$$L_f=L_o·(1+\\alpha·\\Delta T)\\\\\\Delta T=T_f-T_o\\\\\\Delta L=L_f-L_o\\\\\\Delta L=L_o·\\alpha ·\\Delta T$$', '<p>sd</p>', 0, 52, '2023-04-27 18:46:02', '2023-06-14 18:09:15'),
(337, 'Formula fundamental', '$$\\frac{ºC}{5}=\\frac{ºF-32º}{9}=\\frac{ºK-273º}{5}$$', NULL, 0, 51, '2023-04-28 17:14:06', '2023-04-28 17:14:06'),
(335, 'Dilatación Superficial', '$$S_f=S_o·(1+\\beta·\\Delta T)\\\\\\beta=2·\\alpha\\\\\\Delta S=S_f-S_o\\:;\\\\\\Delta S=S_o·\\beta·\\Delta T$$', '<p>SU</p>', 0, 52, '2023-04-27 19:26:27', '2023-06-14 18:10:36'),
(336, 'Dilatación Cúbica', '$$V_f=V_o·(1+\\gamma·\\Delta T)\\\\\\gamma=3·\\alpha\\\\\\Delta V=V_f-V_o\\\\\\Delta V=V_o·\\gamma·\\Delta T$$', '<p>ed</p>', 0, 52, '2023-04-27 19:48:44', '2023-06-14 18:11:44'),
(338, 'Conversión de  ºF a ºC', '$$ºC=\\frac{5}{9}(ºF-32)$$', NULL, 0, 51, '2023-04-28 17:18:32', '2023-05-26 15:59:42'),
(339, 'Conversión de ºK a ºC', '$$ºC=ºK-273º$$', NULL, 0, 51, '2023-04-28 17:19:49', '2023-04-28 18:43:05'),
(340, 'Conversión de  ºC a ºF', '$$ºF=\\frac{9}{5}ºC+32º$$', NULL, 0, 51, '2023-04-28 18:46:39', '2023-04-28 18:50:14'),
(341, 'Conversión de ºK a ºF', '$$ºF=\\frac{9}{5}(ºK-273)+32º$$', NULL, 0, 51, '2023-04-28 18:49:47', '2023-05-26 16:00:09'),
