(344, 'Magnitudes Fundamentales', '$$Longitud:[l]=L\\\\Tiempo:\\:[t]=T\\\\Masa:\\:[m]=M\\\\Intensidad\\:de\\:corriente:[I]=I\\\\Intensidad\\:luminosa:[Il]=J\\\\Cantidad\\:de\\:sustancia:[n]=N$$', NULL, 0, 90, '2023-04-28 19:44:41', '2023-05-02 16:14:01'),
(345, 'Magnitudes Derivadas', '$$Velocidad:\\:[v]=LT^{-1}\\\\Aceleración:[a]=LT^{-2}\\\\Fuerza\\:y\\:peso:[F]=MLT^{-2}\\\\Trabajo\\:y\\:energía:[W;E]=ML^2T^{-2}\\\\Potencia:[P]=ML^2T^{-1}\\\\Area:[A]=L^2\\\\Volumen:[V]=L^3\\\\Densidad:[\\delta]=ML^{-3} \\\\Presión:[P]=ML^{-1}T^{-2}$$', NULL, 0, 90, '2023-04-28 19:58:56', '2023-04-29 02:20:07'),
(346, 'Divisibilidad del 8', '$$Los\\:últimos\\:tres\\:dígitos\\:deben\\:ser\\:múltiplos\\:de\\:ocho\\:o\\:iguales\\:a\\:0$$', '<p>El criterio de divisibilidad del 8 establece que un número es divisible por 8 si los tres últimos dígitos del número son divisibles por 8.&nbsp;</p><p>Por ejemplo:</p><p>1.232 es divisible por 8 porque los tres últimos dígitos (232) son divisibles por 8.</p>', 0, 48, '2023-05-02 16:09:39', '2023-05-02 16:09:39'),
(347, 'Divisibilidad del 9', '$$La\\:suma\\:de\\:los\\:dígitos\\:debe\\:ser\\:un\\:múltiplo\\:de\\:9$$', '<p>El criterio de divisibilidad del 9 establece que un número es divisible por 9 si la suma de sus dígitos es múltiplo de 9.&nbsp;</p><p>Por ejemplo:</p><p>2610 es divisible por 9 porque la suma de sus dígitos (2 + 6 + 1 + 0) es igual a 9, que es múltiplo de 9.</p>', 0, 48, '2023-05-02 16:12:32', '2023-05-02 16:12:32'),
(348, 'Divisibilidad del 10', '$$Para\\:que\\:un\\:número\\:sea\\:divisible\\:entre\\:diez\\:solo\\:debe\\:terminar\\:en\\:0$$', '<p>El criterio de divisibilidad del 10 establece que un número es divisible por 10 cuando termina en cero.&nbsp;</p><p>Por ejemplo:</p><p>120 es divisible por 10 porque termina en cero.</p>', 0, 48, '2023-05-02 16:14:53', '2023-05-02 16:14:53'),
(349, 'Divisibilidad del 11', '$$Un\\:número\\:es\\:divisible\\:entre\\:11\\:cuando\\:la\\:suma\\:de\\:los\\:números\\:que\\:ocupan\\:la\\:posición\\:par\\:menos\\:la\\:suma\\:de\\:los\\:números\\:que\\:ocupan\\:la\\:posición\\:impar\\:es\\:igual\\:a\\:0\\:o\\:a\\:un\\:número\\:múltiplo\\:de\\:11.$$', '<p>El criterio de divisibilidad del 11 establece que un número es divisible por 11 si la diferencia entre la suma de los dígitos en posiciones impares y la suma de los dígitos en posiciones pares es un múltiplo de 11. Por ejemplo, el número 4186 es divisible por 11 porque (4 + 8) - (1 + 6) = 5, que es un múltiplo de 11.</p>', 0, 48, '2023-05-02 16:24:09', '2023-05-02 16:24:09'),
(350, 'Divisibilidad del 12', '$$Un\\:número\\:es\\:divisible\\:por\\:12\\:si\\:es\\:divisible\\:por\\:3\\:y\\:por\\:4\\:al\\:mismo\\:tiempo.$$', '<p>El criterio de divisibilidad del 12 establece que un número es divisible por 12 si es divisible por 3 y por 4 al mismo tiempo. Por ejemplo, el número 216 es divisible por 12 porque es divisible por 3 (2 + 1 + 6 = 9, que es un múltiplo de 3) y por 4 (termina en 16, que es un múltiplo de 4)</p>', 0, 48, '2023-05-02 16:28:03', '2023-05-02 16:28:03'),
(351, 'Divisibilidad del 13', '$$Un\\:número\\:es\\:divisible\\:entre\\:13\\:hay\\:que\\:restar\\:el\\:número\\:sin\\:la\\:cifra\\:de\\:las\\:unidades\\:y\\:9\\:veces\\:la\\:cifra\\:de\\:las\\:unidades.\\:Si\\:esa\\:resta\\:tiene\\:como\\:resultado\\:0\\:múltiplo\\:de\\:13\\:entonces\\:el\\:número\\:es\\:divisible\\:entre\\:13.$$', '<p>El criterio de divisibilidad del 13 establece que un número es divisible por 13 si al restar al número sin la cifra de las unidades la cifra de las unidades multiplicada por 9, el resultado es igual a un múltiplo de 13 (eso incluye al 0 también). Por ejemplo, para verificar si el número 123 es divisible por 13, se resta 3 x 9 = 27 de 12 y se obtiene 96. Como 96 no es un múltiplo de 13, el número 123 no es divisible por 13.</p>', 0, 48, '2023-05-02 16:32:08', '2023-05-02 16:32:08'),
(352, 'Trabajo realizado por una fuerza', '$$W= F·d\\:\\:;Si\\:Festá\\:en\\:direción\\\\del\\:desplazamiento\\\\W=F·cos\\theta·d;\\:Si\\:F\\:forma\\:un\\:ángulo\\:\\theta\\\\respecto\\:del\\:desplazamiento$$', NULL, 0, 85, '2023-05-02 16:37:44', '2023-05-02 16:49:32');
INSERT INTO `formulas` (`id`, `nombre`, `formula`, `detalle`, `indice`, `tema_id`, `created_at`, `updated_at`) VALUES
(353, 'Energía mecánica', '$$E_m=E_c+E_p\\\\E_c=\\frac{1}{2}·m·V^2;\\:\\:\\:E_p=m·g.h$$', NULL, 0, 85, '2023-05-02 16:55:56', '2023-05-02 16:55:56'),
(354, 'Energía Cinética', '$$E_c=\\frac{1}2·m·V^2$$', NULL, 0, 83, '2023-05-02 17:31:35', '2023-08-04 16:16:20'),
(355, 'Energía potencial', '$$E_p= m·g·h$$', NULL, 0, 83, '2023-05-02 17:31:41', '2023-08-04 16:18:07'),
(356, 'Conservación de la energía (si no hay rozamiento)', '$$E_{m_o}=E_{m_f}\\\\E_{c_o}+E_{p_o}=E_{c_f}+E_{p_f}\\\\\\frac{1}{2}mV_o^2+mgh_o=\\frac{1}2mV_f^2+mgh_f$$', NULL, 0, 83, '2023-05-02 17:32:21', '2023-08-04 16:28:59'),
(357, 'Ley de Coulomb', '$$F=K\\cdot \\frac {q_1\\cdot q_2}{r^2}\\\\q_1,q_2=Cargas\\:electricas\\\\r=Distancia\\\\K=Constante$$', '<p>La Ley de Coulomb es una ley de la física que describe la interacción entre partículas cargadas eléctricamente. Establece que la fuerza entre dos cargas puntuales es directamente proporcional al producto de sus cargas e inversamente proporcional al cuadrado de la distancia entre ellas.&nbsp;</p>', 0, 62, '2023-05-03 19:28:45', '2023-05-04 18:50:27'),
(358, 'Potencia en función del trabajo y el tiempo', '$$P=\\frac{W}{t}$$', NULL, 0, 85, '2023-05-04 15:53:53', '2023-05-04 15:53:53'),
(359, 'Potencia en función de la fuerza y la velocidad', '$$P=F·V$$', NULL, 0, 85, '2023-05-04 15:55:51', '2023-05-04 15:55:51'),
(360, 'Equilibrio térmico', '$$Q_1+Q_2+...+Q_n= 0$$', NULL, 0, 74, '2023-05-04 16:02:20', '2023-05-04 16:02:20'),
(361, 'RELACION PRESION DE VAPOR', '$$P_A=X_A\\cdot P_O$$', '<p>SN</p>', 0, 92, '2023-05-04 16:07:43', '2023-05-04 16:07:43'),
(362, 'Temperatura de equilibrio de un sistema de dos sustancias', '$$m_1Ce_1(T-T_1)+m_2Ce_2(T-T_2)=0\\\\T=\\frac{m_1Ce_1T_1+m_2Ce_2T_2}{m_1Ce_1+m_2Ce_2}$$', NULL, 0, 74, '2023-05-04 16:14:45', '2023-05-04 16:14:45'),
(363, 'TEMPERATURA DE EBULLICION', '$$T_b=K_b*\\overline m  :\\\\ K_b=1.86\\:\\left[\\frac{K_{gr}\\cdot ºC}{mol}\\right]$$', '<p>sn</p>', 0, 92, '2023-05-04 16:19:11', '2023-05-05 22:45:16'),
(364, 'TEMPERATURA DE CONGELAMIENTO', '$$T_c=K_c\\cdot\\overline m\\:\\:\\:\\:;\\:K_c=0.52\\:\\left[\\frac{K_{gr}\\cdot ºC}{mol}\\right]$$', '<p>sn</p>', 0, 92, '2023-05-04 16:23:50', '2023-05-05 22:46:35'),
(365, 'En serie', '$$\\frac{1}{Ce}=\\frac{1}{C_1}+\\frac{1}{C_2}+...+\\frac{1}{C_n}\\\\q_T=q_1=q_2=.....=q_n\\\\\\Delta V=V_1+V_2+....+V_n$$', NULL, 0, 67, '2023-05-04 16:39:14', '2023-05-04 16:39:14'),
