(507, 'Fuerza magnética sobre una carga', '$$\\vec{F}=B·[q]·V \\\\si\\:B,FyV ,son\\:perpendiculares\\:entre\\:sí;\\\\además\\:B\\:y\\:Vestén\\:en\\:un\\:plano\\\\\\vec{F}=B·[q]·V·sin\\theta\\\\Si\\:B\\:y\\:V\\:forman\\:un\\:ángulo\\:\\theta\\neq 90º\\\\en\\:un\\:mismo\\:plano$$', NULL, 0, 73, '2023-05-29 17:00:39', '2023-05-29 17:00:39'),
(508, 'Movimiento de una carga en un campo uniforme', '$$Radio:\\:\\:r=\\frac{m·V}{[q]·B}\\\\Velocidad\\:angular:w=\\frac{[q]}{m}·B\\\\Periodo:T=\\frac{2\\pi ·m}{q·B}$$', NULL, 0, 73, '2023-05-29 17:11:59', '2023-05-29 18:28:36'),
