(514, 'Poleas ( Opción 1 ) :   a=?  ;   T=?', '$$Si\\:m_1>m_2\\\\a=g\\left(\\frac{m_1-m_2}{m_1+m_2}\\right)\\\\T=\\frac{2gm_1m_2}{m_1+m_2}$$', NULL, 0, 61, '2023-06-01 23:53:05', '2023-06-02 23:03:13'),
(515, 'Poleas ( Opción 2 ) :   a=?  ;   T=?', '$$Si\\:m_2>m_1\\\\a=g\\left(\\frac{m_2-m_1}{m_1+m_2}\\right)\\\\T=\\frac{2gm_1m_2}{m_1+m_2}$$', NULL, 0, 61, '2023-06-02 23:04:01', '2023-06-02 23:04:01'),
(516, 'Poleas ( Opción 3 ) :   a=?  ;   T=?', '$$m_1+m_2\\:estan\\:a\\:la\\:derecha\\\\Si\\:m_1+m_2>m_3\\\\a=g\\left(\\frac{m_1+m_2-m_3}{m_1+m_2+m_3}\\right)\\\\T_1=\\frac{2gm_1m_3}{m_1+m_2+m_3}\\\\T_2=\\frac{2gm_3(m_1+m_2)}{m_1+m_2+m_3}$$', NULL, 0, 61, '2023-06-03 00:02:16', '2023-06-24 00:05:50'),
(517, 'Sean los Vértices de un  polígono cerrado de \'\'n\'\' lados', '$$P_1(a_1,b_1),P_2(a_2,b_2),P_3(a_3,b_3)..P_n(a_n,b_n)\\\\A=\\frac{1}2[(a_1b_2+a_2b_3+..+a_{n-1}b_n+a_nb_1)-\\\\(b_1a_2+b_2a_3+..+b_{n-1}a_n+b_na_1)]$$', NULL, 0, 7, '2023-06-03 16:22:51', '2023-06-03 16:45:58'),
