(522, 'Area de un triángulo por la fómula de Heron', '$$sean\\:a,b\\:y\\:c\\:los\\:lados\\:de\\:un\\:triángulo\\\\S=\\frac{a+b+c}{2} \\\\A=\\frac{1}{2}\\sqrt{(S-a)·(S-b)·(S-c)}$$', NULL, 0, 37, '2023-06-05 16:57:20', '2023-06-05 17:01:44'),
(523, 'Ley de los nudos', '$$La\\:suma\\:algebráica\\:de\\:la\\:intensidades\\\\de\\:corriente\\:que\\:concurren\\:en\\:un\\:nudo\\\\es\\:igual\\:a\\:cero. \\\\El\\:convenio\\:de\\:signos,es\\:el\\:siguiente:\\\\ Las\\:corrienetes\\:que\\:se\\:dirigen\\:al\\:nudo\\\\son\\:positivas(+),\\:y\\:las\\:que\\:salen\\\\del\\:nudo\\:son\\:negativas(-)\\\\\\sum I=0$$', NULL, 0, 69, '2023-06-07 19:06:18', '2023-06-07 19:17:05'),
(524, 'Ley de la mallas', '$$En\\:todo\\:circuito\\:cerrado,la\\:suma\\\\algebráica\\:de\\:las\\:fuerzas\\:electromotrices\\\\en\\:el\\:existente\\:es\\:igual\\:a\\:la\\:suma\\\\algebráica\\:de\\:todas\\:las\\:caídas\\:de\\\\tensión\\:\\:R·I.\\:\\:es\\:decir:\\\\\\sum \\epsilon =\\sum R·I$$', NULL, 0, 69, '2023-06-07 19:33:59', '2023-06-07 19:33:59'),
(525, '1ra.Regla para la aplicación de las leyes de Kirchhoff', '$$Asumir\\:un\\:sentido\\:para\\:la\\:corriente\\\\en\\:cada\\:conductor\\:del\\:circuito.\\\\No\\:importa\\:si\\:el\\:sentido\\:asumido\\\\es\\:el\\:verdadero\\:o\\:no.$$', NULL, 0, 69, '2023-06-07 19:51:54', '2023-06-07 19:57:26'),
(526, '2da.Regla en la aplicación de la s leyes de Kirchhoff', '$$Elegir\\:un\\:sentido\\:para\\:recorrer\\\\la\\:malla\\:(semi-circuito)\\:ya\\:sea\\\\el\\:sentido\\:de\\:;las\\:manecillas\\:del\\:reloj\\\\o\\:contrario\\:a\\:él.La\\:caída\\:de tensión\\:R·I\\\\ se\\:considera\\:como\\:positivo,si\\:pasamos\\\\por\\:esta\\:resistencia\\:en\\:el\\:sentido\\:asignado\\\\para\\:la\\:corriente\\:de\\:la\\:primera\\:regla\\:y\\:negativo\\:sifluyeensentidocontrario$$', NULL, 0, 69, '2023-06-07 20:04:11', '2023-06-07 22:47:06'),
(527, 'Area de un polígono regular', '$$\\theta\\:\\:ángulo\\:central:\\:\\:\\theta =\\frac{360º}{n}\\:\\:;\\:n=lados\\\\Lado\\:del\\:poligono:\\:\\:l=2·r·sin(\\frac{\\theta }{2})\\\\Apotema\\::a_p=r·cos(\\frac{\\theta}{2})\\\\Perímetro\\::P=n·l=2·n·r·sin(\\frac{\\theta}{2})\\\\Area: A=\\frac{1}{2}·P·a_p=\\frac{n}{2}·r^2·sin\\theta$$', NULL, 0, 28, '2023-06-08 01:56:06', '2023-06-08 01:56:06'),
(528, 'Area de un sector de circunferencia', '$$A= \\frac{\\theta ·r^2}{2}\\\\P=2·r+\\theta·r$$', NULL, 0, 28, '2023-06-08 02:07:13', '2023-06-08 02:07:13'),
(529, 'Integral de la raiz entre la resta de una constante al cuadrado y una función al cuadrado', '$$\\int \\:\\sqrt{a^2-u^2}du=\\frac{1}{2}u\\sqrt{a^2-u^2}+\\frac{1}{2}a^2\\cdot arcsen\\frac{u}{a}+c$$', '<p>&nbsp;</p><p>La formula para la integral de la raiz entre la resta de una constante al cuadrado y una función al cuadrado es:</p><p>∫√(a^2 - u^2) du = (½)u √(a^2 - u^2) + (½)a^2*arcsin(u/a) + C</p><p>Si necesitas ayuda de un profesor contacta con ITE +591 75553338</p>', 0, 25, '2023-06-12 22:52:07', '2023-06-12 22:52:07');
INSERT INTO `formulas` (`id`, `nombre`, `formula`, `detalle`, `indice`, `tema_id`, `created_at`, `updated_at`) VALUES
(530, 'Integral de la raiz entre la suma o resta de una funcion al cuadrado y una constante al cuadrado', '$$\\int \\:\\sqrt{u^2\\pm a^2}du=\\frac{1}{2}u\\sqrt{u^2\\pm a^2}\\pm \\frac{1}{2}a^2\\ln \\left|u+\\sqrt{u^2\\pm a^2}\\right|+c$$', '<p>&nbsp;</p><p>La formula para la integral de la raiz entre la suma o resta de una función al cuadrado y una constante al cuadrado es:</p><p>∫√(u^2 ± a^2) du = (½)u √(u^2 ± a^2) ± (½)a*ln|u+√(u^2 ± a^2)| + C.</p><p>Si necesitas ayuda de un profesor contacta con ITE +591 75553338</p>', 0, 25, '2023-06-12 23:34:23', '2023-06-12 23:34:23'),
(531, 'Integral de la raiz entre la suma o resta de una funcion al cuadrado y una constante al cuadrado', '$$\\int \\:\\sqrt{u^2\\pm a^2}du=\\frac{1}{2}u\\sqrt{u^2\\pm a^2}\\pm \\frac{1}{2}a^2\\ln \\left|u+\\sqrt{u^2\\pm a^2}\\right|+c$$', '<p>&nbsp;</p><p>La formula para la integral de la raiz entre la suma o resta de una función al cuadrado y una constante al cuadrado es:</p><p>∫√(u^2 ± a^2) du = (½)u √(u^2 ± a^2) ± (½)a*ln|u+√(u^2 ± a^2)| + C.</p><p>Si necesitas ayuda de un profesor contacta con ITE +591 75553338</p>', 0, 25, '2023-06-12 23:34:42', '2023-06-12 23:34:42'),
(532, 'Ecuación cuadrática incompleta:ax2+bx=0', '$$ax^2+bx=0\\\\x_1=0;\\:\\:\\:x_2=-\\frac{b}{a}$$', NULL, 0, 3, '2023-06-14 19:13:42', '2023-06-14 19:13:42'),
(533, 'Ecuación cuadrática incompleta:ax2+c=0', '$$ax^2+c=0\\\\x=\\pm\\sqrt{-\\frac{c}{a}}\\\\$$', NULL, 0, 3, '2023-06-14 19:25:53', '2023-06-19 15:57:04'),
(539, 'Función inversa f(x)= arc cosx', '$$f\\left(x\\right)=cos^{-1}x\\\\f\'(x)=-\\frac{1}{\\sqrt{1-x^2}}$$', NULL, 0, 24, '2023-06-20 16:18:45', '2023-06-20 18:25:17'),
(534, 'Poleas ( Opción 4 ) : a=? ; T=?', '$$Si\\:m_3>m_1+m_2\\\\a=g\\left(\\frac{m_3-m_2-m_1}{m_1+m_2+m_3}\\right)\\\\T_1=\\frac{2gm_1m_3}{m_1+m_2+m_3}\\\\T_2=\\frac{2gm_3(m_1+m_2)}{m_1+m_2+m_3}$$', NULL, 0, 61, '2023-06-15 01:30:57', '2023-06-15 01:30:57'),
(535, 'Area y perímetro de un segmento de circunferencia', '$$A=\\frac{r^2}{2}·(\\theta -sin\\theta ) \\\\P=2r·(1+sin\\frac{\\theta}{2})$$', NULL, 0, 28, '2023-06-15 15:59:14', '2023-06-15 15:59:14'),
(536, 'Plano horizontal ( Opción 1 )  : a=?  ;  T=?', '$$Si\\:m_1>m_2\\\\a=g\\left(\\frac{m_1-\\mu m_2}{m_1+m_2}\\right)\\\\T=\\frac{gm_1m_2(1+\\mu)}{m_1+m_2}$$', NULL, 0, 61, '2023-06-17 00:38:48', '2023-06-17 01:24:35'),
(537, 'Plano horizontal ( Opción 2 ) : a=?  ;  T=?', '$$Si\\:m_2>m_1\\\\a=-g\\left(\\frac{m_1+\\mu m_2}{m_1+m_2}\\right)\\\\T=\\frac{gm_1m_2(1-\\mu)}{m_1+m_2}$$', NULL, 0, 61, '2023-06-17 01:23:27', '2023-06-17 01:23:27'),
(538, 'Plano inclinado ( Opción 1 )  : a=?  ;  T=?', '$$m_1\\:esta\\:a\\:la\\:derecha\\\\Si\\:m_1>m_2\\\\a=g\\left(\\frac{m_1-m_2( \\sin \\left(\\alpha \\right)+\\mu \\cos \\left(\\alpha \\right) )}{m_1+m_2}\\right)\\\\T=\\frac{gm_1m_2( 1+\\sin \\left(\\alpha \\right)+\\mu \\cos \\left(\\alpha \\right) )}{m_1+m_2}$$', NULL, 0, 61, '2023-06-17 22:50:56', '2023-06-24 00:06:23'),
(540, 'Función inversa f(x)= arc tan x', '$$f(x)=tan^{-1}x\\\\f\'(x)=\\frac{1}{1+x^2}$$', NULL, 0, 24, '2023-06-20 16:21:45', '2023-06-20 18:26:04'),
(541, 'Función inversa f(x)= arc cot x', '$$f(x)=cot^{-1}x\\\\f\'(x)=-\\frac{1}{1+x^2}$$', NULL, 0, 24, '2023-06-20 16:23:33', '2023-06-20 18:26:40'),
(542, 'Función inversa f(x)= arc sec x', '$$f(x)=sec^{-1}x\\\\f\'(x)=\\frac{1}{x\\sqrt{x^2-1}}$$', NULL, 0, 24, '2023-06-20 16:25:05', '2023-06-20 18:27:32'),
(543, 'Función inversa f(x)= arc csc x', '$$f(x)=csc^{-1}x\\\\f\'(x)=-\\frac{1}{x\\sqrt{x^2-1}}$$', NULL, 0, 24, '2023-06-20 16:26:36', '2023-06-20 18:28:37'),
(546, 'Velocidad Final al Cuadrada', '$$Vf^2=Vo^2+2ad$$', '<p>,</p><p>La formula de velocidad final al cuadrado para movimiento rectilíneo uniforme variado es:</p><p>vf2 = vo2 + 2ad</p><p>Donde:&nbsp;<br>vf2 = velocidad final al cuadrado&nbsp;<br>vo2 = velocidad inicial al cuadrado&nbsp;<br>a = aceleración&nbsp;<br>d = distancia</p><p>Si necesitas ayuda de un profesor contacta con ITE +591 75553338</p>', 0, 55, '2023-06-22 23:36:47', '2023-06-22 23:36:47'),
(547, 'Velocidad Inicial al Cuadrado', '$$Vo^2=Vf^2-2ad$$', '<p>.</p><p>La formula de velocidad inicial al cuadrado para movimiento rectilíneo uniforme variado es:</p><p>vo2 = vf2 - 2ad</p><p>Donde:&nbsp;<br>vo2 = velocidad inicial al cuadrado&nbsp;<br>vf2 = velocidad final al cuadrado&nbsp;<br>a = aceleración&nbsp;<br>d = distancia</p><p>Si necesitas ayuda de un profesor contacta con ITE +591 75553338</p>', 0, 55, '2023-06-22 23:40:24', '2023-06-22 23:40:24'),
(548, 'Plano inclinado ( Opción 2 )  : a=?  ;  T=?', '$$m_1\\:esta\\:a\\:la\\:derecha\\\\Si\\:m_2>m_1\\\\a=g\\left(\\frac{m_2( \\sin \\left(\\alpha \\right)-\\mu \\cos \\left(\\alpha \\right) )-m_1}{m_1+m_2}\\right)\\\\T=\\frac{gm_1m_2( 1+\\sin \\left(\\alpha \\right)-\\mu \\cos \\left(\\alpha \\right) )}{m_1+m_2}$$', NULL, 0, 61, '2023-06-24 00:00:22', '2023-06-24 00:16:46'),
(549, 'sin(90-x)', '$$sin(\\frac{\\pi}2-x)=\\cos x$$', NULL, 0, 13, '2023-07-01 16:29:33', '2023-07-01 16:33:47'),
