(551, 'cos (90-x)', '$$\\cos (\\frac{\\pi}2-x)=\\sin x$$', NULL, 0, 13, '2023-07-01 16:36:36', '2023-07-01 16:41:35'),
(552, 'cos (90+x)', '$$\\cos(\\frac{\\pi}2+x)=-\\sin x$$', NULL, 0, 13, '2023-07-01 16:38:47', '2023-07-01 16:42:50'),
(553, 'tan (90-x) =', '$$\\tan(\\frac{\\pi}2-x) =\\cot x$$', NULL, 0, 13, '2023-07-01 16:45:24', '2023-07-01 16:45:24'),
(554, 'tan (90+x)', '$$\\tan(\\frac{\\pi}2+x) =-\\cot x$$', NULL, 0, 13, '2023-07-01 16:47:37', '2023-07-01 16:47:37'),
